mapper.select:

SELECT COUNT(*) 
  FROM member 
 WHERE member_id = #{member_id}

mapper.update:

UPDATE member 
   SET member_name=#{member_name}, member_email=#{member_email}, modi_date=now() 
 WHERE member_id=#{member_id}


UPDATE member 
   SET modi_date=now(), del_yn='Y'
 WHERE member_id=#{member_id}

mapper.select:
SELECT member_role 
  FROM member_role 
 WHERE member_id = #{member_id}

mapper.update:

UPDATE member_role 
   SET member_roles=#{member_roles}, moid_id=#{modi_id}, modi_name=#{modi_name}, modi_date=now() 
 WHERE member_id=#{member_id}


UPDATE member_role 
   SET modi_id=#{modi_id}, modi_name=#{modi_name}, modi_date=now(), del_yn='Y'
 WHERE member_id=#{member_id}
